mapper.select:
        select a.* from t_rt_txn a
        inner join(
        select hash from t_rt_txn
        where 1=1
         and block_hash=#{block_hash} 
         and status=#{status} 
         and `from`=#{from} 
         and `to`=#{to} 
         and (`from`=#{address} or `to`=#{address}) 

        order by t desc limit #{offset},#{limit}
        ) b on a.hash = b.hash

        select count(1) from t_rt_txn
        where 1=1
         and block_hash=#{block_hash} 
         and status=#{status} 
         and `from`=#{from} 
         and `to`=#{to} 
         and (`from`=#{address} or `to`=#{address})
